def FindPossibility(n):
    if n==1:
        return "YES"
    stack = []
    while (n != 1):
        if n % 2 == 0:
            n = n//2
            #stack.append(n)
        else:
            n = 3*n + 1
            #stack.append(n)
        if n in stack:
            return "NO"
        else:
            stack.append(n)
    #print(stack)
    return "YES"

